mapper.update:
 UPDATE 
    CODEMNG
SET 
    CDLVL = #{cdlv}, UPCD = #{highcd}, CDNAME = #{cdnm}, USEYN = #{use}
WHERE 
    CDNO = #{cdno}

UPDATE 
    ITEMLIST
SET 
    ITEMNAME = #{itemnm}, MADENMCD = #{brand}, ITEMUNITCD = #{itemunitcd}, USEYN = #{use}
WHERE 
    ITEMCD = #{itemcd}

UPDATE INITEMLIST
SET INSAMT = #{qow}
WHERE INSITEMLISTCD = #{whcd}
